header {
    position: relative;
    background-color: var(--green);
    color: var(--blue);
    padding: 1.5rem 0 0 var(--padding-side);
}

@media (max-width: 750px) {
    header {
        overflow: visible;
        padding-bottom: 1rem;
    }
}

.legal {
    display: flex;
    gap: 1.75rem;
    flex-wrap: wrap;
    align-items: center;
    width: calc(100% - 1.5rem * 2 - var(--padding-side));
    max-width: calc(var(--max-width) - 1.5rem * 6 - var(--padding-side));
    min-height: 1.5rem;
}

header .insta {
